    for i in range(n):
        row = list(map(int, stdin.readline().strip().split()))
        matrix.append(row)

    def find_position(matrix, target):
        for i in range(n):
            for j in range(n):
                if matrix[i][j] == target:
                    return (i, j)
        return None

    def bfs(matrix, start, end):
        visited = set()
        queue = [(start, 0)]

        while queue:
            (x, y), distance = queue.pop(0)
            if (x, y) in visited:
                continue
            visited.add((x, y))

            if matrix[x][y] == end:
                return distance

            for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                nx, ny = x + dx, y + dy
                if 0 <= nx < n and 0 <= ny < n and (nx, ny) not in visited:
                    queue.append(((nx, ny), distance + 1))

        return -1

    start_pos = find_position(matrix, 1)
    end_pos = find_position(matrix, k)

    if start_pos is None or end_pos is None:
        return -1

    distance = bfs(matrix, start_pos, end_pos)

    return distance if distance != -1 else "Impossible"

