    n = int(stdin.split("\n")[0])
    temps = list(map(int, stdin.split("\n")[1].split()))

    # Check if there is a valid solution
    for i in range(n - 1):
        if temps[i] > temps[i + 1]:
            continue
        else:
            if i > 0 and temps[i - 1] < temps[i + 1]:
                return "impossible"

    # Find the starting point of the increasing sequence
    start_index = 0
    for i in range(n - 1):
        if temps[i] <= temps[i + 1]:
            start_index = i
            break

    # Reverse the first part of the sequence
    temps[:start_index + 1] = temps[:start_index + 1][::-1]

    return " ".join(map(str, temps))

