def solution(stdin: str) -> str:
    key_phrase = stdin.split('\n')[0]
    plaintext = stdin.split('\n')[1]

    # Generate encryption key
    key = ""
    for char in key_phrase:
        if char != 'q' and char not in key:
            key += char
    for char in 'abcdefghijklmnoprstuvwxyz':
        if char not in key:
            key += char

    # Create 5x5 table
    table = []
    for i in range(5):
        row = []
        for j in range(5):
            row.append(key[i * 5 + j])
        table.append(row)

    # Encrypt plaintext
    encrypted_text = ""
    plaintext = plaintext.replace(" ", "")
    i = 0
    while i < len(plaintext):
        if i < len(plaintext) - 1 and plaintext[i] == plaintext[i + 1]:
            encrypted_text += plaintext[i].upper() + 'X'
            plaintext = plaintext[:i + 1] + 'x' + plaintext[i + 1:]
            i += 1
        else:
            if i == len(plaintext) - 1 and plaintext[i] == 'x':
                encrypted_text += 'X'
            elif i < len(plaintext) - 1:
                digraph = plaintext[i:i + 2]
                if digraph[0] == digraph[1]:
                    encrypted_text += digraph[0].upper() + 'X'
                    plaintext = plaintext[:i + 1] + 'x' + plaintext[i + 1:]
                else:
                    row1 = -1
                    row2 = -1
                    col1 = -1
                    col2 = -1
                    for j in range(5):
                        if digraph[0] in table[j]:
                            row1 = j
                            col1 = table[j].index(digraph[0])
                        if digraph[1] in table[j]:
                            row2 = j
                            col2 = table[j].index(digraph[1])
                    if row1 == row2:
                        encrypted_text += table[row1][(col1 + 1) % 5] + table[row2][(col2 + 1) % 5]
                    elif col1 == col2:
                        encrypted_text += table[(row1 + 1) % 5][col1] + table[(row2 + 1) % 5][col2]
                    else:
                        encrypted_text += table[row1][col2] + table[row2][col1]
                    i += 1
            i += 1

    return encrypted_text.upper()

