def solution(stdin: str) -> str:
    n, s = stdin.split("\n")
    n = int(n)
    MOD = 1000000007

    def count_seq(s: str) -> int:
        n = len(s)
        dp = [0] * (n + 1)
        dp[0] = 1

        for i in range(n):
            if s[i] == "(":
                for j in range(i + 1, n):
                    if s[j] == ")":
                        dp[j + 1] += dp[i]
            else:
                for j in range(i + 1, n):
                    if s[j] == "(":
                        dp[j + 1] += dp[i]

        return dp[n] % MOD

    def helper(s: str, n: int) -> int:
        if n == 0:
            if s == "":
                return 1
            else:
                return 0

        if n == 1:
            if s == "()" or s == "(1+1)":
                return 1
            else:
                return 0

        if len(s) < 2 * n:
            return 0

        count = 0
        for i in range(len(s) - 2 * n + 1):
            if s[i] == "(" and s[i + 2 * n - 1] == ")":
                count += count_seq(s[i + 1:i + 2 * n - 1])
                count %= MOD

        return count % MOD

    return helper(s, n)

