        reversed_rows = matrix[::-1]
        reversed_cols = [row[::-1] for row in matrix]
        return matrix == reversed_rows and matrix == reversed_cols

    def construct_matrix(numbers, n):
        if len(numbers) != n * n:
            return None

        matrix = [[0] * n for _ in range(n)]
        used = set()

        def backtrack(row):
            if row == n:
                return True

            for col in range(n):
                if col in used:
                    continue

                matrix[row][col] = numbers[row * n + col]
                used.add(col)

                if backtrack(row + 1):
                    return True

                used.remove(col)
                matrix[row][col] = 0

            return False

        if backtrack(0):
            return matrix
        else:
            return None

    n, *numbers = [int(x) for x in stdin.split()]
    matrix = construct_matrix(numbers, n)

    if matrix and is_palindromic(matrix):
        return "YES\n" + "\n".join(" ".join(str(x) for x in row) for row in matrix)
    else:
        return "NO"

