    def construct_tree(n, d, k):
        if d * 2 > n:
            return False
        if d >= n // 2:
            return True

        level_size = 1
        next_level_size = 1
        level = 0

        tree = [[] for _ in range(n)]

        for i in range(n):
            if len(tree[i]) < k:
                for j in range(level_size):
                    if len(tree[i]) >= k:
                        break
                    if i + 1 < n and len(tree[i + 1]) < k:
                        tree[i].append(i + 1)
                        tree[i + 1].append(i)
                        next_level_size += 1
            if i + 1 == n:
                break
            if len(tree[i]) == k:
                level_size -= 1
            if i + 1 == level_size * (level + 1):
                level += 1
                next_level_size += level_size

        if next_level_size > d:
            return False
        return tree

    tree = construct_tree(n, d, k)

    if not tree:
        return "NO"
    else:
        output = ["YES"]
        for i in range(n - 1):
            for j in range(len(tree[i])):
                output.append(f"{i + 1} {tree[i][j] + 1}")
        return "\n".join(output)

