    n, m, k = map(int, stdin.readline().split())
    grid = [[int(x) for x in stdin.readline().split()] for _ in range(n)]

    mod = 10**9 + 7

    def xor_sum(i: int, j: int) -> int:
        xor_sum = 0
        while i <= n and j <= m:
            xor_sum ^= grid[i - 1][j - 1]
            if i == n and j == m:
                break
            if j == m:
                i += 1
            else:
                j += 1
        return xor_sum

    paths = [[0] * (m + 1) for _ in range(n + 1)]
    paths[1][1] = 1

    for i in range(1, n + 1):
        for j in range(1, m + 1):
            if i == n and j == m:
                continue
            if i < n:
                paths[i + 1][j] += paths[i][j]
                paths[i + 1][j] %= mod
            if j < m:
                paths[i][j + 1] += paths[i][j]
                paths[i][j + 1] %= mod

    result = 0
    for i in range(1, n + 1):
        for j in range(1, m + 1):
            if xor_sum(i, j) == k:
                result += paths[i - 1][j - 1]
                result %= mod

    return str(result)

