def solution(stdin: str) -> str:
    n, m = map(int, stdin.split()[0:2])
    a = list(map(int, stdin.split()[2:2 + n]))
    special_offers = []
    for i in range(m):
        x, y, w = map(int, stdin.split()[2 + n + 3 * i:2 + n + 3 * (i + 1)])
        special_offers.append([x, y, w])

    def kruskal(n, a, special_offers):
        parent = [i for i in range(n)]
        rank = [0] * n

        def find(x):
            if x != parent[x]:
                parent[x] = find(parent[x])
            return parent[x]

        def union(x, y):
            x_root = find(x)
            y_root = find(y)
            if x_root == y_root:
                return False
            if rank[x_root] > rank[y_root]:
                parent[y_root] = x_root
            else:
                parent[x_root] = y_root
                if rank[x_root] == rank[y_root]:
                    rank[y_root] += 1
            return True

        edges = []
        for i in range(n):
            for j in range(i + 1, n):
                edges.append([a[i] + a[j], i, j])

        for x, y, w in special_offers:
            edges.append([w, x, y])

        edges.sort()
        cost = 0
        for edge in edges:
            w, x, y = edge
            if union(x, y):
                cost += w
        return cost

    return str(kruskal(n, a, special_offers))

