    def binary_search(rain, x, index):
        left, right = 0, len(rain) - 1
        while left <= right:
            mid = (left + right) // 2
            if rain[mid][0] <= x < rain[mid][1] and rain[mid][2] == -1:
                rain[mid][2] = index
                return True
            elif rain[mid][0] <= x < rain[mid][1] and rain[mid][2] != -1:
                return False
            elif x < rain[mid][0]:
                right = mid - 1
            else:
                left = mid + 1
        return False

    def update_umbrellas(umbrellas, rain, index):
        for i in range(len(rain)):
            if rain[i][2] != -1:
                umbrellas[rain[i][2]] += 1
                rain[i][2] = -1
        umbrellas[index] -= 1

    def calculate_fatigue(a, umbrellas, rain, umbrella_positions):
        fatigue = 0
        current_umbrella = 0
        for i in range(a + 1):
            if i in umbrella_positions:
                current_umbrella = umbrella_positions.index(i)
            if any(l_i <= i < r_i for l_i, r_i in rain) and umbrellas[current_umbrella] == 0:
                return -1
            fatigue += sum(umbrellas[j] for j in range(len(umbrellas)) if umbrellas[j] > 0)
        return fatigue

    a, n, m = map(int, stdin.readline().split())
    rain = [list(map(int, stdin.readline().split())) + [-1] for _ in range(n)]
