    n, m = map(int, input_list[0].split())
    edges = [list(map(int, edge.split())) for edge in input_list[1:]]

    # Create adjacency list
    adj_list = [[] for _ in range(n+1)]
    for edge in edges:
        v, u = edge
        adj_list[v].append(u)
        adj_list[u].append(v)

    # Depth First Search
    visited = [False] * (n+1)
    connected_components = []

    def dfs(node, component):
        visited[node] = True
        component.append(node)
        for neighbor in adj_list[node]:
            if not visited[neighbor]:
                dfs(neighbor, component)

    for i in range(1, n+1):
        if not visited[i]:
            component = []
            dfs(i, component)
            connected_components.append(component)

    # Count cycles
    cycle_components = 0
    for component in connected_components:
        if len(component) >= 3:
            is_cycle = True
            for i in range(len(component)):
                for j in range(i+1, len(component)):
                    if adj_list[component[i]].count(component[j]) != 1 or adj_list[component[j]].count(component[i]) != 1:
                        is_cycle = False
                        break
                if not is_cycle:
                    break
            if is_cycle:
                cycle_components += 1

    return str(cycle_components)

