    for _ in range(m):
        v, u = map(int, stdin.readline().split())
        edges.add((v, u))

    def is_connected(v, u):
        if v == u:
            return False
        visited = [False] * (n + 1)
        stack = [v]
        visited[v] = True

        while stack:
            vertex = stack.pop()
            for neighbor in (vertex - 1, vertex + 1):
                if 1 <= neighbor <= n and not visited[neighbor] and (vertex, neighbor) in edges:
                    if neighbor == u:
                        return True
                    visited[neighbor] = True
                    stack.append(neighbor)
        return False

    def bfs(v):
        visited = [False] * (n + 1)
        visited[v] = True
        queue = [(v, 0)]

        while queue:
            vertex, distance = queue.pop(0)
            for neighbor in (vertex - 1, vertex + 1):
                if 1 <= neighbor <= n and not visited[neighbor] and (vertex, neighbor) in edges:
                    visited[neighbor] = True
                    queue.append((neighbor, distance + 1))
        return max(visited[1:])

    max_diameter = -1
    for v in range(1, n + 1):
        diameter = bfs(v)
        max_diameter = max(max_diameter, diameter)

    result = [str(max_diameter)]
    if m < n - 1:
        for v in range(1, n + 1):
            for u in range(1, n + 1):
                if (v, u) not in edges and is_connected(v, u):
