def solution(stdin: str) -> str:
    def find(parent, i):
        if parent[i] == i:
            return i
        return find(parent, parent[i])

    def union(parent, rank, x, y):
        xroot = find(parent, x)
        yroot = find(parent, y)

        if xroot == yroot:
            return False
        if rank[xroot] < rank[yroot]:
            parent[xroot] = yroot
        elif rank[xroot] > rank[yroot]:
            parent[yroot] = xroot
        else:
            parent[yroot] = xroot
            rank[xroot] += 1
        return True

    def kruskal(parent, rank, edges):
        mst_cost = 0
        mst_edges = []
        for edge in sorted(edges, key=lambda x: x[2]):
            if union(parent, rank, edge[0], edge[1]):
                mst_cost += edge[2]
                mst_edges.append(edge)
        return mst_cost, mst_edges

    lines = stdin.split('\n')
    n, m = map(int, lines[0].split())
    edges = [list(map(int, line.split())) for line in lines[1:m + 1]]

    parent = [i for i in range(n + 1)]
    rank = [0] * (n + 1)
    mst_cost, mst_edges = kruskal(parent, rank, edges)

    for i in range(m):
        edges[i][2] += 1
        parent = [i for i in range(n + 1)]
        rank = [0] * (n + 1)
        new_mst_cost, new_mst_edges = kruskal(parent, rank, edges)

        if new_mst_cost == mst_cost and len(new_mst_edges) == len(set(mst_edges)):
            return i + 1
        edges[i][2] -= 1

    return 0

