    edges = [list(map(int, stdin.readline().split())) for _ in range(m)]

    parent = [i for i in range(n + 1)]

    def find(vertex):
        if parent[vertex] == vertex:
            return vertex
        parent[vertex] = find(parent[vertex])
        return parent[vertex]

    def union(u, v):
        u_root = find(u)
        v_root = find(v)
        if u_root == v_root:
            return False
        parent[u_root] = v_root
        return True

    result = []
    for u, v in edges:
        if union(u, v):
            result.append((u, v))

    return '\n'.join(' '.join(map(str, edge)) for edge in result)

