    def distance(p1, p2):
        return abs(p1[0] - p2[0]) + abs(p1[1] - p2[1])

    n = int(stdin.readline().strip())
    points = [tuple(map(int, stdin.readline().strip().split())) for _ in range(n)]
    max_level = max(max(p[0], p[1]) for p in points)

    def dfs(start, visited, current_level):
        if current_level > max_level:
            return 0

        if not visited:
            visited = {start}

        total_distance = 0
        next_level = current_level + 1

        for p in points:
            if p in visited or p[0] != p[1]:
                continue

            total_distance += distance(start, p)
            visited.add(p)
            total_distance += dfs(p, visited, next_level)

        return total_distance

    start = min(points, key=lambda p: max(p[0], p[1]))
    return str(dfs(start, set(), 1))

