    a = list(map(int, stdin.split('\n')[1].split()))

    # Calculate the current remainders and their counts
    remainder_counts = [0] * m
    for num in a:
        remainder_counts[num % m] += 1

    # Calculate the required remainder counts
    required_remainder_count = n // m

    # Calculate the minimum number of moves
    min_moves = 0
    for i in range(m):
        if i != 0:
            min_moves += abs(required_remainder_count - remainder_counts[i])
        else:
            min_moves += min(required_remainder_count - remainder_counts[i], remainder_counts[i])

    # Make the required changes
    for i in range(m):
        if i != 0:
            while remainder_counts[i] < required_remainder_count:
                for j in range(n):
                    if a[j] % m == i:
                        a[j] += 1
                        break
                remainder_counts[i] += 1
        else:
            while remainder_counts[i] > required_remainder_count:
                for j in range(n):
                    if a[j] % m == i:
                        a[j] += 1
                        break
                remainder_counts[i] -= 1

    # Return the minimum number of moves and the resulting array
    return f"{min_moves}\n{' '.join(map(str, a))}"

