    def is_valid_sudoku(board):
        for i in range(9):
            for j in range(9):
                num = board[i][j]
                for k in range(9):
                    if board[i][k] == num and k != j:
                        return False
                    if board[k][j] == num and k != i:
                        return False
                block_x = i // 3
                block_y = j // 3
                for x in range(block_x * 3, block_x * 3 + 3):
                    for y in range(block_y * 3, block_y * 3 + 3):
                        if x != i or y != j:
                            if board[x][y] == num:
                                return False
        return True

    def make_anti_sudoku(board, changed):
        if changed >= 9:
            return board
        for i in range(9):
            for j in range(9):
                if board[i][j] == 0:
                    for num in range(1, 10):
                        board[i][j] = num
                        if is_valid_sudoku(board):
                            result = make_anti_sudoku(board, changed + 1)
                            if result:
                                return result
                    board[i][j] = 0
                    return None
        return board

    t = int(input())
    results = []
    for _ in range(t):
        board = [[int(x) for x in input().strip()] for _ in range(9)]
        result = make_anti_sudoku(board, 0)
        if result:
            results.append("".join(str(x) for row in result for x in row))
        else:
            results.append("No solution")

    return "\n".join(results)

