                continue
            j = i
            cycle = [j]
            while p[j] not in cycle:
                j = p[j]
                cycle.append(j)
            if p[j] == j:
                length = len(cycle) + 1
            else:
                length = len(cycle) - cycle.index(p[j])
            for k in cycle:
                visited.add(k)
        return length

