        cycle_length = find_cycle_length(p)
        return_days = [-1] * n
        for i in range(n):
            if return_days[i] == -1:
                return_day = (i // cycle_length) * cycle_length + i % cycle_length + 1
                return_days[i] = return_day
                for j in range(i + 1, n):
                    if p[j] == i:
                        return_days[j] = return_day
