    A, B, C, D = map(lambda x: list(map(float, x.split())), stdin.split('\n'))

    def dot_product(X, Y):
        return sum(a * b for a, b in zip(X, Y))

    def cross_product(X, Y):
        return [X[1] * Y[2] - X[2] * Y[1], X[2] * Y[0] - X[0] * Y[2], X[0] * Y[1] - X[1] * Y[0]]

    def magnitude(X):
        return (sum(x ** 2 for x in X)) ** 0.5

    AB = [B[0] - A[0], B[1] - A[1], B[2] - A[2]]
    BC = [C[0] - B[0], C[1] - B[1], C[2] - B[2]]
    CD = [D[0] - C[0], D[1] - C[1], D[2] - C[2]]

    X = cross_product(AB, BC)
    Y = cross_product(BC, CD)

    cos_phi = dot_product(X, Y) / (magnitude(X) * magnitude(Y))
    phi = round(math.degrees(math.acos(cos_phi)), 2)

    return str(phi)

