def solution(stdin: str) -> str:
    def move(grid: list, direction: int) -> list:
        def merge(line: list) -> tuple:
            merged = [0] * len(line)
            index = 0
            for i in range(len(line)):
                if line[i] != 0:
                    merged[index] = line[i]
                    index += 1
            return merged, index

        def move_row_left(row: list) -> list:
            merged_row, index = merge(row)
            for i in range(index - 1):
                if merged_row[i] == merged_row[i + 1] and merged_row[i] != 0:
                    merged_row[i] *= 2
                    merged_row[i + 1] = 0
            return merge(merged_row)[0]

        def move_row_right(row: list) -> list:
            return move_row_left(row[::-1])[::-1]

        def move_row_up(row: list) -> list:
            return move_row_left([row[i] for i in range(len(row))])

        def move_row_down(row: list) -> list:
            return move_row_left([row[i] for i in range(len(row) - 1, -1, -1)])

        if direction == 0:  # left
            return [move_row_left(row) for row in grid]
        elif direction == 1:  # up
            return move_row_up([grid[i][j] for j in range(len(grid))])
        elif direction == 2:  # right
            return [move_row_right(row) for row in grid]
        else:  # down
            return move_row_down([grid[i][j] for j in range(len(grid) - 1, -1, -1)])

    def add_random_tile(grid: list) -> list:
        empty_cells = [(i, j) for i in range(len(grid)) for j in range(len(grid)) if grid[i][j] == 0]
        if not empty_cells:
            return grid
        new_tile_value = 2 if random.random() < 0.9 else 4
        empty_cell = random.choice(empty_cells)
        new_grid = [row.copy() for row in grid]
        new_grid[empty_cell[0]][empty_cell[1]] = new_tile_value
        return new_grid

    lines = stdin.split('\n')[:-1]
    grid = [[int(x) for x in line.split()] for line in lines[:-1]]
    direction = int(lines[-1])

    new_grid = move(grid, direction)
    new_grid = add_random_tile(new_grid)
    output = '\n'.join([' '.join(map(str, row)) for row in new_grid])

    return output + '\n'

def solution(stdin: str) -> str:
    input_molecule, input_count = map(str.strip, stdin.split('\n'))
    input_count = int(input_count)
    output_molecule = stdin.split('\n')[1].strip()

    def parse_molecule(molecule: str) -> dict:
        atom_count = {}
        i = 0
        while i < len(molecule):
            if molecule[i].isupper():
                if i + 1 < len(molecule) and molecule[i + 1].isdigit():
                    count = int(molecule[i + 1])
                    i += 2
                else:
                    count = 1
                    i += 1
                atom = molecule[i]
                if atom in atom_count:
                    atom_count[atom] += count
                else:
                    atom_count[atom] = count
            else:
                i += 1
        return atom_count

    input_atoms = parse_molecule(input_molecule)
    output_atoms = parse_molecule(output_molecule)

    def lcm(x: int, y: int) -> int:
        return x * y // gcd(x, y)

    def gcd(x: int, y: int) -> int:
        while y:
            x, y = y, x % y
        return x

    def atoms_to_coefficient(input_atoms: dict, output_atoms: dict) -> int:
        input_atoms_list = list(input_atoms.items())
        output_atoms_list = list(output_atoms.items())
        input_atoms_list.sort(key=lambda x: x[0])
        output_atoms_list.sort(key=lambda x: x[0])

        lcm_coefficient = 1
        for i in range(len(input_atoms_list)):
            if input_atoms_list[i][0] != output_atoms_list[i][0]:
                return 0
            lcm_coefficient = lcm(lcm_coefficient, input_atoms_list[i][1] // output_atoms_list[i][1])

        return lcm_coefficient

    output_count = atoms_to_coefficient(input_atoms, output_atoms) * input_count
    return str(output_count)

