    nums = list(map(int, input().split()))

    if K == 1:
        return str(sum(nums))

    def count_ways(K, nums):
        dp = [0] * 11
        dp[0] = 1

        for num in nums:
            if num == 0:
                continue
            for i in range(num, -1, -1):
                dp[num] += dp[i]

        return dp

    dp = count_ways(K, nums)
    total_ways = dp[K]

    for i in range(1, 11):
        if nums[i - 1] >= K:
            total_ways -= dp[K - i]

    return str(total_ways)

