    def is_transposition(first_melody, second_melody, n):
        transposed_melody = [first_melody[0]]
        for i in range(1, n):
            transposed_melody.append(first_melody[(i + n) % n])
        return transposed_melody == second_melody

    def is_retrograde(first_melody, second_melody, n):
        return first_melody == second_melody[::-1]

    def is_inversion(first_melody, second_melody, n):
        return first_melody[0] == second_melody[0] and all(
            (first_melody[i] - first_melody[0]) * (-1) == (second_melody[i] - second_melody[0])
            for i in range(1, n)
        )

