    first_melody = lines[1].split()
    second_melody = lines[2].split()

    if is_transposition(first_melody, second_melody, n):
        return "Transposition"
    elif is_retrograde(first_melody, second_melody, n):
        return "Retrograde"
    elif is_inversion(first_melody, second_melody, n):
        return "Inversion"
    else:
        return "Nonsense"

