    # Parse the input
    lines = stdin.splitlines()
    M, N = map(int, lines[0].split())
    coins = [list(map(int, line.split())) for line in lines[1:]]

    # Initialize variables
    visited = {(0, 0): 0}
    queue = [(0, 0)]

    # Breadth-first search
    while queue:
        row, col = queue.pop(0)
        for dr, dc in [(-1, 0), (0, -1), (1, 0), (0, 1)]:
            new_row, new_col = row + dr, col + dc
            if 0 <= new_row < M and 0 <= new_col < N:
                height = coins[new_row][new_col]
                if (new_row, new_col) not in visited or height < visited[(new_row, new_col)]:
                    visited[(new_row, new_col)] = height
                    queue.append((new_row, new_col))

    # Find the shortest ladder length
    return str(visited[M - 1, N - 1])

