    # Find the matrix with the most rows
    for R in range(1, N + 1):
        if N % R == 0:
            C = N // R
            if R <= C:
                break

    # Create the matrix and read the message
    matrix = []
    row = 0
    col = 0
    for i in range(N):
        if col == C:
            row += 1
            col = 0
        matrix.append([0] * C)
        matrix[row][col] = message[i]
        col += 1

    # Read the message in column-major order
    decrypted = []
    for col in range(C):
        for row in range(R):
            if matrix[row][col]:
                decrypted.append(matrix[row][col])
    return ''.join(decrypted)

