            x, y, distance = queue.pop(0)
            if x == end_x and y == end_y:
                return distance

            for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                next_x, next_y = x + dx, y + dy
                if is_safe(next_x, next_y) and not visited[next_x][next_y]:
                    visited[next_x][next_y] = True
                    queue.append((next_x, next_y, distance + 1))

        return -1

    start_x, start_y = None, None
    for i in range(n):
        for j in range(m):
            if grid[i][j] == 'S':
                start_x, start_y = i, j
                break

    if start_x is None or start_y is None:
        return "NOT POSSIBLE"

    result = bfs(start_x, start_y, 0, 0)
    return str(result) if result != -1 else "NOT POSSIBLE"

