    for i in range(M + 2, 2 * M + 2):
        frame2.append(lines[i])

    # Check if the silhouette is connected or not
    def is_connected(frame):
        silhouette_pixels = [(row, col) for row in range(M) for col in range(N) if frame[row][col] == C]
        visited = set()

        def dfs(pixel):
            if pixel in visited:
                return
            visited.add(pixel)
            row, col = pixel
            for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                new_row, new_col = row + dy, col + dx
                if 0 <= new_row < M and 0 <= new_col < N and frame[new_row][new_col] == C:
                    dfs((new_row, new_col))

        dfs(silhouette_pixels[0])
        return len(visited) == len(silhouette_pixels)

    connected1 = is_connected(frame1)
    connected2 = is_connected(frame2)

    # Calculate the shift
    shift_row, shift_col = 0, 0
    for row in range(M):
        for col in range(N):
            if frame1[row][col] == C and frame2[row][col] != C:
                shift_row = row
                shift_col = col
                break
        else:
            continue
        break

    # Extrapolate the falcon's movement
    extrapolated_frame = []
    for row in range(M):
        new_row = []
        for col in range(N):
            if connected1 and connected2:
                if frame1[row][col] == C:
                    new_row.append(C)
                else:
                    new_row.append(frame2[row - shift_row][col - shift_col])
            else:
                if frame2[row][col] == C:
