    W, H = map(int, stdin[0].split())
    map_grid = stdin[1:]

    def safe_move(player_pos, direction):
        x, y = player_pos
        if direction == 'up':
            new_pos = (x, y - 1)
        elif direction == 'down':
            new_pos = (x, y + 1)
        elif direction == 'left':
            new_pos = (x - 1, y)
        else:
            new_pos = (x + 1, y)
        return new_pos

    def check_drafts(player_pos, map_grid):
        drafts = []
        x, y = player_pos
        for direction in ['up', 'down', 'left', 'right']:
            new_pos = safe_move(player_pos, direction)
            if new_pos[0] < 0 or new_pos[0] >= W or new_pos[1] < 0 or new_pos[1] >= H:
                continue
            if map_grid[new_pos[1]][new_pos[0]] == 'T':
                drafts.append(direction)
        return drafts

    def collect_gold(player_pos, map_grid):
        collected_gold = 0
        x, y = player_pos
        for direction in ['up', 'down', 'left', 'right']:
            new_pos = safe_move(player_pos, direction)
            if new_pos[0] < 0 or new_pos[0] >= W or new_pos[1] < 0 or new_pos[1] >= H:
                continue
            if map_grid[new_pos[1]][new_pos[0]] == 'G':
                collected_gold += 1
        return collected_gold

    player_pos = None
    for y, row in enumerate(map_grid):
        for x, col in enumerate(row):
            if col == 'P':
                player_pos = (x, y)
                break

    drafts = check_drafts(player_pos, map_grid)
    gold_collected = collect_gold(player_pos, map_grid)

    for direction in drafts:
        new_pos = safe_move(player_pos, direction)
        new_drafts = check_drafts(new_pos, map_grid)
        new_gold_collected = collect_gold(new_pos, map_grid)
        if len(new_drafts) < len(drafts) or (len(new_drafts) == len(drafts) and new_gold_collected > gold_collected):
            gold_collected = new_gold_collected
            player_pos = new_pos
            drafts = new_drafts

    return gold_collected

