    def is_valid_piece(c: str) -> bool:
        return c.upper() in "KQRBNP"

    def is_piece(c: str) -> bool:
        return c.isalpha() and is_valid_piece(c)

    def is_empty(c: str) -> bool:
        return c in ". "

    def is_empty_or_valid_piece(c: str) -> bool:
        return is_empty(c) or is_valid_piece(c)

    def is_pawn(c: str) -> bool:
        return c.upper() == "P"

    def is_king(c: str) -> bool:
        return c.upper() == "K"

    def is_queen(c: str) -> bool:
        return c.upper() == "Q"

    def is_rook(c: str) -> bool:
        return c.upper() == "R"

    def is_bishop(c: str) -> bool:
        return c.upper() == "B"

    def is_knight(c: str) -> bool:
        return c.upper() == "N"

    def is_white_piece(c: str) -> bool:
        return c.isupper()

    def is_black_piece(c: str) -> bool:
        return c.islower()

    def is_white_empty(c: str) -> bool:
        return is_white_piece(c) or is_empty(c)

    def is_black_empty(c: str) -> bool:
        return is_black_piece(c) or is_empty(c)

    def is_outline(c: str) -> bool:
        return c in "+-|:"

    def parse_piece(c: str) -> str:
        return c.upper() if is_pawn(c) else c.upper()

    def parse_position(row: int, col: int) -> str:
        return f"{chr(ord('a') + col)}{8 - row}"

    def find_piece_positions(board: List[List[str]], piece_type: str) -> List[Tuple[int, int]]:
        positions = []
        for row in range(len(board)):
            for col in range(len(board[0])):
                if is_piece(board[row][col]) and board[row][col].upper() == piece_type:
                    positions.append((row, col))
        return positions

    def sort_piece_positions(positions: List[Tuple[int, int]], white: bool) -> List[Tuple[int, int]]:
        return sorted(positions, key=lambda x: x[1] if white else -x[1], reverse=True)

    def describe_piece(board: List[List[str]], row: int, col: int, white: bool) -> str:
        piece_type = parse_piece(board[row][col])
        return f"{piece_type}{parse_position(row, col)}" if (white and is_white_piece(board[row][col])) or (not white and is_black_piece(board[row][col])) else ""

    def describe_pieces(board: List[List[str]], piece_type: str, white: bool) -> str:
        positions = find_piece_positions(board, piece_type)
        sorted_positions = sort_piece_positions(positions, white)
        description = [describe_piece(board, row, col, white) for row, col in sorted_positions]
        return ",".join(filter(lambda x: x, description))

