    n = int(stdin.readline().strip())
    matrix = [list(map(int, stdin.readline().strip().split())) for _ in range(n)]

    def find_cycle(start):
        visited = [False] * n
        cycle = []
        while not visited[start]:
            visited[start] = True
            cycle.append(start)
            start = matrix[start].index(1)
        cycle.append(start)
        return cycle

    def dfs(start, end):
        visited = [False] * n
        stack = [(start, [start])]

        while stack:
            (node, path) = stack.pop()
            if node == end:
                return path
            if not visited[node]:
                visited[node] = True
                for i in range(n):
                    if matrix[node][i] and not visited[i]:
                        stack.append((i, path + [i]))

    cycle = find_cycle(0)
    start, end = cycle[:2]
    path = dfs(start, end)

    result = []
    for i in range(len(path) - 1):
        result.append(f"{path[i]} {path[i + 1]}")
    return "\n".join(result)

