def solution(stdin: str) -> str:
    # Parse input
    input_list = stdin.split("\n")
    n, q = map(int, input_list[0].split())

    # Create Union-Find data structure
    class UnionFind:
        def __init__(self, n):
            self.parent = list(range(n + 1))
            self.rank = [0] * (n + 1)

        def find(self, x):
            if self.parent[x] != x:
                self.parent[x] = self.find(self.parent[x])
            return self.parent[x]

        def union(self, x, y):
            root_x = self.find(x)
            root_y = self.find(y)

            if root_x == root_y:
                return False

            if self.rank[root_x] > self.rank[root_y]:
                self.parent[root_y] = root_x
            else:
                self.parent[root_x] = root_y
                if self.rank[root_x] == self.rank[root_y]:
                    self.rank[root_y] += 1

            return True

        def get_size(self, x):
            return -self.rank[self.find(x)]

    uf = UnionFind(n)

    # Process queries
    output_list = []
    for i in range(1, q + 1):
        query = input_list[i].split()
        if query[0] == 't':
            a, b = map(int, query[1:])
            uf.union(a, b)
        else:
            a = int(query[1])
            output_list.append(uf.get_size(a))

    # Return output
    return "\n".join(map(str, output_list))

