def solution(stdin: str) -> str:
    from math import sqrt

    def distance(a, b):
        return sqrt((a[0] - b[0])**2 + (a[1] - b[1])**2)

    def find_centroid(points):
        x, y = 0, 0
        for a, b in points:
            x += a
            y += b
        return x / len(points), y / len(points)

    def similarity_transform(points, centroid, scale):
        new_points = []
        for a, b in points:
            x = (a - centroid[0]) * scale + centroid[0]
            y = (b - centroid[1]) * scale + centroid[1]
            new_points.append((x, y))
        return new_points

    def find_min_coords(points):
        min_x, min_y = float('inf'), float('inf')
        for a, b in points:
            min_x = min(min_x, a)
            min_y = min(min_y, b)
        return min_x, min_y

    def translate_to_origin(points, min_x, min_y):
        new_points = []
        for a, b in points:
            x = a - min_x
            y = b - min_y
            new_points.append((x, y))
        return new_points

    lines = stdin.split('\n')
    num_points = int(lines[0])
    points = [(float(x), float(y)) for x, y in (line.split() for line in lines[1:num_points+1])]
    area = int(lines[num_points+1])

    centroid = find_centroid(points)
    scale = sqrt(area / sum(distance(a, b) for a, b in zip(points, points[1:] + points[:1])))

    transformed_points = similarity_transform(points, centroid, scale)
    min_x, min_y = find_min_coords(transformed_points)

    final_points = translate_to_origin(transformed_points, min_x, min_y)

    return '\n'.join(f'{x:.6f} {y:.6f}' for x, y in final_points) + '\n'

