    n, m, d = map(int, input().split())
    c = list(map(int, input().split()))

    platforms = [[] for _ in range(m + 1)]
    platforms[0] = [0]
    platforms[m + 1] = [n + 1]
    for i in range(m):
        platforms[i + 1].append(sum(platforms[i]) + c[i])

    def is_reachable(platforms, d, n):
        platforms = [p for p in platforms if p[1] - p[0] > 1]
        visited = [False] * (n + 2)

        def dfs(pos, depth):
            if visited[pos]:
                return False
            visited[pos] = True
            if depth == 0:
                return True

            for p in platforms:
                if p[0] <= pos <= p[1] - d:
                    if dfs(pos + d, depth - 1):
                        return True
            return False

        return dfs(0, d)

    if is_reachable(platforms, d, n):
        return "YES\n" + " ".join(map(str, platforms))
    else:
        return "NO\n"

