    understanding = [0] * M

    # Calculate the minimum amount of money needed to achieve the objective
    min_money = float('inf')

    def is_achievable(understanding):
        return all(level >= X for level, X in zip(understanding, [X or -1] * M))

    def dfs(current_money, current_understanding, stack):
        nonlocal min_money

        if current_money > min_money:
            return

        if is_achievable(current_understanding):
            min_money = min(min_money, current_money)
            return

        for i in range(N):
            if stack[i] == -1:
                stack[i] = A[i].count(1) - 1

            while stack[i] >= 0:
                stack[i] -= 1
                new_understanding = [level + A[i][j] for level, j in zip(current_understanding, range(M))]
                dfs(current_money + C[i], new_understanding, stack)

        stack[stack.index(max(stack))] -= 1
        dfs(current_money, current_understanding, stack)

