    reordered_sequence = list(map(int, stdin.split("\n")[1].split()))
    sequence = []

    def add_to_sequence(x: int):
        if x not in sequence:
            sequence.append(x)

    def find_last_number(x: int) -> int:
        for i in range(len(sequence) - 1, -1, -1):
            if sequence[i] % x == 0:
                return i
        return -1

    def find_next_number(x: int) -> int:
        if x % 2 == 0:
            return x // 2
        else:
            return x * 3 // 2

    x = reordered_sequence[0]
    add_to_sequence(x)

    for i in range(1, n):
        j = find_last_number(x)
        if j != -1:
            sequence[j] = find_next_number(x)
            add_to_sequence(find_next_number(x))
        else:
            for k in range(len(sequence) - 1, -1, -1):
                if sequence[k] % 2 == 0:
                    sequence[k] = find_next_number(x)
                    add_to_sequence(find_next_number(x))
                    break
            else:
                add_to_sequence(find_next_number(x))
        x = sequence[j + 1]

    return " ".join(map(str, sequence))

