    def find_subset(points: list) -> int:
        max_size = 0
        for i in range(len(points)):
            subset_size = 1
            for j in range(i + 1, len(points)):
                if is_power_of_two(abs(points[i] - points[j])):
                    subset_size += 1
            max_size = max(max_size, subset_size)
        return max_size

    def find_subset_with_coordinates(points: list) -> list:
        subset = []
        for i in range(len(points)):
            subset_size = 1
            for j in range(i + 1, len(points)):
                if is_power_of_two(abs(points[i] - points[j])):
                    subset_size += 1
            if subset_size == max_size:
                subset.append(points[i])
        return subset

    n = int(stdin.splitlines()[0])
    points = [int(x) for x in stdin.splitlines()[1].split()]

    max_size = find_subset(points)
    subset = find_subset_with_coordinates(points)

    output = "\n".join(str(x) for x in subset)
    return f"{max_size}\n{output}"


