    matrix = []
    for _ in range(n):
        row = list(map(int, input().split()))
        matrix.append(row)

    def is_good_matrix(matrix):
        for i in range(1, n + 1):
            for j in range(1, m + 1):
                if matrix[i - 1][j - 1] != (i - 1) * m + j:
                    return False
        return True

    def cyclic_shift(matrix, col):
        for i in range(n):
            matrix[i][col - 1] = matrix[(i - 1) % n][col - 1]

    min_moves = 0
    while not is_good_matrix(matrix):
        min_moves += 1
        good_col = 0
        for j in range(1, m + 1):
            if matrix[0][j - 1] == (0 * m + j):
                good_col = j
                break
        cyclic_shift(matrix, good_col)

    return str(min_moves)


