def solution(stdin: str) -> str:
    n = int(stdin.splitlines()[0])
    a = list(map(int, stdin.splitlines()[1].split()))

    def is_valid(blocks):
        for i in range(len(blocks)):
            for j in range(i + 1, len(blocks)):
                if blocks[j][0] <= blocks[i][1] and blocks[i][0] <= blocks[j][1]:
                    return False
        return True

    def find_blocks(a, n, target_sum):
        blocks = []
        for i in range(n):
            for j in range(i + 1, n + 1):
                current_sum = sum(a[i:j])
                if current_sum == target_sum:
                    blocks.append((i, j - 1))
        return blocks

    target_sum = sum(a) // n
    blocks = find_blocks(a, n, target_sum)
    max_blocks = len(blocks)

    while not is_valid(blocks):
        max_blocks += 1
        if max_blocks > n:
            break
        blocks = find_blocks(a, n, target_sum)

    return max_blocks, *blocks


