def solution(stdin: str) -> str:
    N, M, a, b = 0, 0, [], []

    for line in stdin.split('\n'):
        if N == 0 and M == 0:
            N, M = map(int, line.split())
        else:
            a.append(int(line.split()[0]))
            b.append(int(line.split()[1]))

    graph = [[0] * (N+1) for _ in range(N+1)]

    for i in range(M):
        graph[a[i]][b[i]] = 1
        graph[b[i]][a[i]] = 1

    def dfs(graph, visited, node, path, result):
        visited[node] = True
        path.append(node)

        for i in range(1, N+1):
            if graph[node][i] == 1 and not visited[i]:
                dfs(graph, visited, i, path, result)

        if len(path) == N:
            result.append(path.copy())

        path.pop()
        visited[node] = False

    visited = [False] * (N+1)
    path = []
    result = []
    dfs(graph, visited, 1, path, result)

    return len(result)

