    input_lines = stdin.splitlines()
    n, m = map(int, input_lines[0].split())
    grid = [list(line) for line in input_lines[1:]]

    def is_valid_star(x, y, size):
        if x < 1 or x > n or y < 1 or y > m:
            return False
        for i in range(x - size, x + size + 1):
            for j in range(y - size, y + size + 1):
                if i < 1 or i > n or j < 1 or j > m:
                    continue
                if grid[i - 1][j - 1] == '*':
                    return False
        return True

    def dfs(x, y, size, count):
        if count >= n * m:
            return True
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            new_x, new_y = x + dx, y + dy
            if is_valid_star(new_x, new_y, size):
                grid[new_x - 1][new_y - 1] = '*'
                if dfs(new_x, new_y, size, count + 1):
                    return True
                grid[new_x - 1][new_y - 1] = '.'
        return False

    count = 0
    for i in range(1, n + 1):
        for j in range(1, m + 1):
            if grid[i - 1][j - 1] == '*':
                count += dfs(i, j, 1, 1)

    if count == n * m:
        for i in range(n):
            for j in range(m):
                if grid[i][j] == '*':
                    print(i + 1, j + 1, 1)
    else:
        print(-1)

