def solution(stdin: str) -> str:
    input_data = stdin.splitlines()
    n, m = map(int, input_data[0].split())
    edges = [tuple(map(int, edge.split())) for edge in input_data[1:]]
    adj_matrix = [[0 for _ in range(n)] for _ in range(n)]

    for u, v in edges:
        adj_matrix[u - 1][v - 1] = 1
        adj_matrix[v - 1][u - 1] = 1

    visited = [False] * n

    def dfs(node):
        visited[node] = True
        for neighbor in range(n):
            if adj_matrix[node][neighbor] == 1 and not visited[neighbor]:
                if dfs(neighbor):
                    return True
        return False

    def has_cycle(node, visited, rec_stack):
        visited[node] = True
        rec_stack[node] = True

        for neighbor in range(n):
            if adj_matrix[node][neighbor] == 1 and not visited[neighbor]:
                if has_cycle(neighbor, visited, rec_stack):
                    return True
            elif rec_stack[neighbor]:
                return True

        rec_stack[node] = False
        return False

    for node in range(n):
        if not visited[node] and dfs(node):
            return "NO"

    for node in range(n):
        visited = [False] * n
        rec_stack = [False] * n
        if has_cycle(node, visited, rec_stack):
            return "NO"

    result = ""
    for edge in edges:
        result += "1" if edge[1] > edge[0] else "0"

    return "YES\n" + result

