def solution(stdin: str) -> str:
    n = int(stdin)
    a = [[] for _ in range(n)]
    for i in range(n):
        a[i] = list(map(int, stdin.split()))
        a[i].pop(0)
        a[i].pop(0)
        a[i] = [a[i][0], a[i][1]]

    def find(i, parent, memo):
        if i == parent:
            return i
        if memo[i] != -1:
            return memo[i]
        memo[i] = find(parent[i], parent, memo)
        return memo[i]

    def union(i, j, parent, rank, memo):
        pi = find(i, parent, memo)
        pj = find(j, parent, memo)
        if pi == pj:
            return False
        if rank[pi] < rank[pj]:
            parent[pi] = pj
        elif rank[pi] > rank[pj]:
            parent[pj] = pi
        else:
            parent[pj] = pi
            rank[pi] += 1
        return True

    parent = [-1] * n
    rank = [0] * n
    memo = [-1] * n

    for i in range(n):
        for j in range(i + 1, n):
            if a[i][0] == a[j][1]:
                union(i, j, parent, rank, memo)
            elif a[i][1] == a[j][0]:
                union(i, j, parent, rank, memo)

    def find_next(i):
        pi = find(i, parent, memo)
        for j in range(i + 1, n):
            pj = find(j, parent, memo)
            if pj == pi:
                return j
        return -1

    next_kid = [0] * n
    for i in range(n):
        next_kid[i] = find_next(i)

    order = [0] * n
    order[0] = p = 1
    for i in range(1, n):
        p = next_kid[p - 1]
        order[i] = p

    return " ".join(map(str, order))

