    n, m = map(int, input().split())
    a = list(map(int, input().split()))
    edges = []
    for _ in range(m):
        x, y, w = map(int, input().split())
        edges.append((x - 1, y - 1, w))

    def kruskal(edges):
        edges.sort(key=lambda x: x[2])
        parent = list(range(n))

        def find(x):
            if parent[x] != x:
                parent[x] = find(parent[x])
            return parent[x]

        def union(x, y):
            parent[find(x)] = find(y)

        mst = 0
        for x, y, w in edges:
            if find(x) != find(y):
                union(x, y)
                mst += w
                if len(set(find(i) for i in range(n))) == 1:
                    return mst

        return mst

    return str(kruskal(edges))


