        for j in range(max(0, i - 3), min(i + 4, n)):
            if s[j] == 'R':
                red += 1
            elif s[j] == 'G':
                green += 1
            else:
                blue += 1
        return red, green, blue

    def recolor(s, i):
        new_s = list(s)
        new_s[i] = s[(i + 1) % n] if s[i] == 'R' else ('B' if s[i] == 'G' else 'R')
        return ''.join(new_s)

    min_recolors = float('inf')
    min_nice_garland = ''
    for i in range(n):
        if s[i] == 'R':
            red, green, blue = count_neighbors(s, i)
            if red % 2 == 1:
                continue
            new_s = recolor(s, i)
            if is_nice(new_s):
                recolors = red // 2
                if recolors < min_recolors:
                    min_recolors = recolors
                    min_nice_garland = new_s

