    adjacency_list = [[] for _ in range(n)]

    # Build the adjacency list
    for u, v in edges:
        adjacency_list[u - 1].append(v - 1)
        adjacency_list[v - 1].append(u - 1)

    # Find the spanning tree with maximum degree
    def kruskal(adjacency_list):
        parent = list(range(n))
        rank = [0] * n
        edges = sorted(edges, key=lambda x: x[2])
        result = []
        for u, v, _ in edges:
            if find(parent, u) != find(parent, v):
                result.append((u, v))
                union(parent, rank, u, v)
                if len(result) == n - 1:
                    break
        return result

    def find(parent, i):
        if parent[i] != i:
            parent[i] = find(parent, parent[i])
        return parent[i]

    def union(parent, rank, u, v):
        u_root = find(parent, u)
        v_root = find(parent, v)
        if rank[u_root] < rank[v_root]:
            parent[u_root] = v_root
        elif rank[u_root] > rank[v_root]:
            parent[v_root] = u_root
        else:
            parent[v_root] = u_root
            rank[u_root] += 1

    return '\n'.join(map(lambda x: f"{x[0] + 1} {x[1] + 1}", kruskal(adjacency_list)))

