        if 0 < i < n and 0 < j < n:
            return True
        return False

    def get_possible_moves(i, j, n):
        moves = []
        if is_valid_move(i - 1, j - 1, n):
            moves.append((i - 1, j - 1))
        if is_valid_move(i - 1, j, n):
            moves.append((i - 1, j))
        if is_valid_move(i - 1, j + 1, n):
            moves.append((i - 1, j + 1))
        if is_valid_move(i, j - 1, n):
            moves.append((i, j - 1))
        if is_valid_move(i, j + 1, n):
            moves.append((i, j + 1))
        if is_valid_move(i + 1, j - 1, n):
            moves.append((i + 1, j - 1))
        if is_valid_move(i + 1, j, n):
            moves.append((i + 1, j))
        if is_valid_move(i + 1, j + 1, n):
            moves.append((i + 1, j + 1))
        return moves

    def minimize_moves(board, n):
        count = 0
        while sum(sum(row) for row in board) != n * n:
            min_moves = n * n
            for i in range(n):
                for j in range(n):
                    if board[i][j] == 0:
                        continue
                    moves = get_possible_moves(i, j, n)
                    for move in moves:
                        new_board = [[0] * n for _ in range(n)]
                        for x in range(n):
                            for y in range(n):
                                new_board[x][y] = board[x][y]
                        new_board[i][j] = 0
                        new_board[move[0]][move[1]] = board[i][j]
                        temp_count = minimize_moves(new_board, n)
                        min_moves = min(min_moves, temp_count + 1)
            count += min_moves
            if count > n * n:
                return n * n
            for i in range(n):
                for j in range(n):
                    if board[i][j] == 0:
                        continue
                    moves = get_possible_moves(i, j, n)
                    for move in moves:
                        new_board = [[0] * n for _ in range(n)]
                        for x in range(n):
                            for y in range(n):
                                new_board[x][y] = board[x][y]
                        new_board[i][j] = 0
                        new_board[move[0]][move[1]] = board[i][j]
                        temp_board = [row[:] for row in new_board]
                        temp_count = minimize_moves(temp_board, n)
                        if temp_count <= min_moves:
                            board[move[0]][move[1]] = board[i][j]
                            board[i][j] = 0
        return count

    t = int(stdin.readline())
