    def distance(x1, y1, x2, y2):
        return abs(x1 - x2) + abs(y1 - y2)

    n = int(stdin[0])
    key_points = []
    for i in range(1, n + 1):
        x, y = map(int, stdin[i].split())
        key_points.append((x, y))

    key_points.sort(key=lambda x: (x[0] + x[1], x[0], x[1]))
    level = 1
    visited = set()
    total_distance = 0
    visited.add((0, 0))

    def visit_all(level):
        nonlocal visited, total_distance
        for i in range(len(key_points)):
            if key_points[i][0] + key_points[i][1] == level:
                if key_points[i] not in visited:
                    visited.add(key_points[i])
                    total_distance += distance(0, 0, key_points[i][0], key_points[i][1])

    while level <= 2 * n and len(visited) < n:
        visit_all(level)
        level += 1

    return total_distance

