def solution(stdin: str) -> str:
    input_list = stdin.split('\n')
    n = int(input_list[0])
    a = list(input_list[1])
    b = list(input_list[2])

    def swap(i, j, a, b):
        a[i - 1], b[j - 1] = b[j - 1], a[i - 1]

    def preprocess_move(i, c, a):
        a[i - 1] = c

    def is_equal(a, b):
        return ''.join(a) == ''.join(b)

    if n % 2 == 0:
        for i in range(1, n + 1):
            preprocess_move(i, b[n - i], a)
    else:
        for i in range(1, n + 1, 2):
            preprocess_move(i, b[n - i], a)

    if is_equal(a, b):
        return 0

    min_changes = float('inf')

    for i in range(1, n + 1):
        for j in range(1, n + 1):
            for k in range(1, n + 1):
                for l in range(1, n + 1):
                    swap(i, j, a, b)
                    swap(k, l, a, b)
                    if is_equal(a, b):
                        min_changes = min(min_changes, 4)
                        swap(i, j, a, b)
                        swap(k, l, a, b)
                    else:
                        swap(i, j, a, b)
                        swap(k, l, a, b)
                        min_changes = min(min_changes, 4)
                        swap(i, j, a, b)
                        swap(k, l, a, b)
                    swap(i, j, a, b)
                    swap(k, l, a, b)

    return min_changes

