        for u2, v2, w in tree:
            if u2 == u:
                max_weight = max(max_weight, find_max_path_weight(v, v2, visited) + w)
            elif v2 == v:
                max_weight = max(max_weight, find_max_path_weight(u, u2, visited) + w)
        visited[(u, v)] = max_weight
        return max_weight

    result = []
    for q in queries:
        visited = {}
        count = 0
        for u in range(1, n + 1):
            for v in range(u + 1, n + 1):
                if find_max_path_weight(u, v, visited) <= q:
                    count += 1
        result.append(count)

    return ' '.join(map(str, result))

