    N = int(stdin.split()[0])
    T, A = [], []
    for i in range(1, N + 1):
        T.append(int(stdin.split()[i]))
        A.append(int(stdin.split()[N + i]))

    def gcd(a, b):
        while b:
            a, b = b, a % b
        return a

    def lcm(a, b):
        return a * b // gcd(a, b)

    def find_min_votes(T, A, N):
        for i in range(1, N):
            T[i] = lcm(T[i - 1], T[i])
            A[i] = lcm(A[i - 1], A[i])
        return max(T[N - 1], A[N - 1])

    return str(find_min_votes(T, A, N))

